const slides=document.querySelectorAll(".slide"),dots=document.querySelectorAll(".dot");let index=0;const activeSlide=e=>{for(slide of slides)slide.classList.remove("active");slides[e].classList.add("active")},activeDot=e=>{for(dot of dots)dot.classList.remove("active");dots[e].classList.add("active")},prepareCurrentSlide=e=>{activeSlide(e),activeDot(e)},nextSlide=()=>{index==slides.length-1?(index=0,prepareCurrentSlide(index)):(index++,prepareCurrentSlide(index))},prevSlide=()=>{0==index?(index=slides.length-1,prepareCurrentSlide(index)):(index--,prepareCurrentSlide(index))};dots.forEach((e,i)=>{e.addEventListener("click",()=>{index=i,prepareCurrentSlide(index)})}),function(){const e=document.querySelector(".burger"),i=document.querySelector(".menu_mobile");e.addEventListener("click",()=>{e.classList.toggle("burger_active"),i.classList.toggle("menu_active")})}();