const slides=document.querySelectorAll(".slide"),dots=document.querySelectorAll(".dot");let index=0;const activeSlide=e=>{for(slide of slides)slide.classList.remove("active");slides[e].classList.add("active")},activeDot=e=>{for(dot of dots)dot.classList.remove("active");dots[e].classList.add("active")},prepareCurrentSlide=e=>{activeSlide(e),activeDot(e)},nextSlide=()=>{index==slides.length-1?(index=0,prepareCurrentSlide(index)):(index++,prepareCurrentSlide(index))},prevSlide=()=>{0==index?(index=slides.length-1,prepareCurrentSlide(index)):(index--,prepareCurrentSlide(index))};dots.forEach((e,d)=>{e.addEventListener("click",()=>{index=d,prepareCurrentSlide(index)})});